import string
# Remove punctuations
def removePuncStr(s):
    for c in string.punctuation:
        s = s.replace(c," ")
    return s

def removePunc(text_array):
    return [removePuncStr(h) for h in text_array]

# Remove numbers
def removeNumbersStr(s):
  for c in range(10):
    n = str(c)
    s = s.replace(n,"")
  return s

def removeNumbers(text_array):
    return [removeNumbersStr(h) for h in text_array]
